import React, { useState, useEffect } from 'react';
import './resources/css/App.css';
// import other CSS files and images
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import hotels from './data/hotels.json';
import advantages from './data/advantages.json';
import HotelCard from './HotelCard';
import AdvantageCard from './AdvantageCard';
import ThankYou from './ThankYou';

function App() {
  const [guestName, setGuestName] = useState('');
  const [selectedHotel, setSelectedHotel] = useState('');
  const [date, setDate] = useState(() => {
    const current = new Date();
    const year = current.getFullYear();
    const month = (current.getMonth() + 1).toString().padStart(2, '0');
    const day = current.getDate().toString().padStart(2, '0');
    return `${year}-${month}-${day}`;
  });
  const [numberOfNights, setNumberOfNights] = useState(10);
  const [numberOfGuests, setNumberOfGuests] = useState(2);
  const [breakfast, setBreakfast] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    let totalPriceCalc = 0;
    if (selectedHotel) {
      totalPriceCalc = selectedHotel.price * numberOfNights * numberOfGuests;
      if (breakfast) {
        totalPriceCalc += selectedHotel.breakfast * numberOfNights * numberOfGuests;
      }
    }
    setTotalPrice(totalPriceCalc);
  }, [numberOfNights, numberOfGuests, selectedHotel, breakfast]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!guestName || !selectedHotel) {
      alert("Please complete all fields.");
      return;
    }
    const message = {
      guestName,
      selectedHotel: selectedHotel ? selectedHotel.name : '',
      checkInDate: date,
      numberOfNights,
      customMessage: `Thank you, ${guestName}! Your reservation at ${selectedHotel.name} has been confirmed. Your stay will begin on ${date} and end on ${new Date(new Date(date).getTime() + numberOfNights * 24 * 60 * 60 * 1000).toLocaleDateString()}. We wish you a pleasant stay!`
    };
    setSubmitted(message);
  };

  if (submitted) {
    return <ThankYou message={submitted} />;
  }

  return (
    <div className="App">
      <header className="grid headerFixed">
        <div className='logo'>
          <a href="#home">
            <img src={plane_logo} className='headerimage' alt="onboard_logo" />
          </a>
        </div>
        <div className="navlist">
          <a className='buttona' href="#home"><div className='buttondiv'>Home</div></a>
          <a className='buttona' href="#ourhotels"><div className='buttondiv'>Our Hotels</div></a>
          <a className='buttona' href="#whybookwithus"><div className='buttondiv'>Why book with us?</div></a>
          <a className='buttona booknowbutton' href="#booknow"><div className='buttondiv booknowbuttondiv'>Book now!</div></a>
        </div>
      </header>
      <main>
        <section className='introduction' id='home'>
          <Carousel autoPlay interval={3000} infiniteLoop useKeyboardArrows showThumbs={false}>
            <div>
              <img src={beach} className='image' alt="beach" />
            </div>
            <div>
              <img src={hotel} className='image' alt="hotel" />
            </div>
            <div>
              <img src={room} className='image' alt="room" />
            </div>
          </Carousel>
          <a href="#booknow"><button className='introductionbutton'>Book your dream hotel today!</button></a>
          <h1>Discover your dreams with us. OnBoard!</h1>
        </section>

        <section className='sectiontitle' id='ourhotels'>
          <div><h1>Trending Holiday Destinations</h1></div>
          <div><p>| Discover some of our most popular tourist destinations for an incredible experience.</p></div>
        </section>
        <section className='ourhotels'>
          <div className='gridhotels'>
            {hotels.map(hotel => (
              <HotelCard key={hotel.name} hotel={hotel} />
            ))}
          </div>
        </section>

        <section className='description'>
          <img src={beachsunset} className='descriptionimage' alt="beachsunset" />
          <img src={sun} className='descriptionsun' alt="sun" />
          <p className='descriptiondata'>Welcome to <b>OnBoard</b>, your passport to a world of unforgettable stays. <br></br>
            We bring together an unparalleled collection of luxury, boutique, and unique hotels from around the globe,
            all at the click of a button. With us, it's not just about booking a room; it's about immersing yourself
            in a new destination. Our handpicked hotels are more than just a place to sleep - they are experiences in
            themselves, each with its own unique story to tell. <br></br><br></br>Whether you're dreaming of a relaxing beachside resort,
            a chic city-center boutique, a quaint countryside inn, or a luxurious five-star hotel, we have you covered. </p>
        </section>

        <section className='sectiontitle' id='whybookwithus'>
          <div><h1>Why book with us?</h1></div>
          <div><p>| Here are some of the advantages of choosing our service for your next getaway</p></div>
        </section>
        <section className='whybookwithus'>
          <div className='gridadvantages'>
            {advantages.map(advantage => (
              <AdvantageCard key={advantage.title} advantage={advantage} />
            ))}
          </div>
        </section>

        <section className='formsection'>
          <form className='form' onSubmit={handleSubmit} id='booknow'>
            <div className='gridform'>
              <div className='formelement'>
                <label htmlFor='name'>Guest Name:</label>
                <input type="text" id="name" name="name" value={guestName} onChange={(e) => setGuestName(e.target.value)} />
              </div>
              <div className='formelement'>
                <label htmlFor='hotel'>Hotel:</label>
                <select id="hotel" name="hotel" value={selectedHotel} onChange={(e) => setSelectedHotel(hotels.find(hotel => hotel.name === e.target.value))}>
                  <option value="">Select a hotel</option>
                  {hotels.map(hotel => (
                    <option key={hotel.name} value={hotel.name}>{hotel.name}</option>
                  ))}
                </select>
              </div>
              <div className='formelement'>
                <label htmlFor='date'>Date:</label>
                <input type="date" id="date" name="date" value={date} onChange={(e) => setDate(e.target.value)} />
              </div>
              <div className='formelement'>
                <label htmlFor='numberOfNights'>Number of Nights:</label>
                <input type="number" id="numberOfNights" name="numberOfNights" value={numberOfNights} onChange={(e) => setNumberOfNights(e.target.value)} />
              </div>
              <div className='formelement'>
                <label htmlFor='numberOfGuests'>Number of Guests:</label>
                <input type="number" id="numberOfGuests" name="numberOfGuests" value={numberOfGuests} onChange={(e) => setNumberOfGuests(e.target.value)} />
              </div>
              <div className='formelement'>
                <label htmlFor='breakfast'>Include Breakfast:</label>
                <input type="checkbox" id="breakfast" name="breakfast" checked={breakfast} onChange={(e) => setBreakfast(e.target.checked)} />
              </div>
              <div className='formelement'>
                <label htmlFor='totalPrice'>Total Price:</label>
                <input type="text" id="totalPrice" name="totalPrice" value={`$${totalPrice.toFixed(2)}`} readOnly />
              </div>
              <div className='formelement'>
                <button type="submit">Submit</button>
              </div>
            </div>
          </form>
        </section>
      </main>
      <footer className='footer'>
        <div className="footer-container">
          <div className="footer-item">
            <a className="footer-link" href="#">Privacy Policy</a>
          </div>
          <div className="footer-item">
            <a className="footer-link" href="#">Terms of Service</a>
          </div>
          <div className="footer-item">
            <a className="footer-link" href="#">Contact Us</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
